mapper.select:
SELECT
-- 级联表的字段 ,xinxi.xinxi_name as xinxiName ,xinxi.xinxi_photo as xinxiPhoto ,xinxi.xinxi_video as xinxiVideo ,xinxi.xinxi_types as xinxiTypes ,xinxi.zan_number as zanNumber ,xinxi.cai_number as caiNumber ,xinxi.xinxi_clicknum as xinxiClicknum ,xinxi.shangxia_types as shangxiaTypes ,xinxi.xinxi_content as xinxiContent ,yonghu.yonghu_uuid_number as yonghuUuidNumber ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.school as school ,yonghu.shiyong_types as shiyongTypes ,yonghu.yonghu_email as yonghuEmail
FROM xinxi_liuyan a left JOIN xinxi xinxi ON a.xinxi_id = xinxi.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.xinxi_id = #{params.xinxiId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.xinxi_liuyan_text like CONCAT('%',#{params.xinxiLiuyanText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.xinxi_id IS NOT NULL
and xinxi.xinxi_name like CONCAT('%',#{params.xinxiName},'%')
and xinxi.xinxi_video like CONCAT('%',#{params.xinxiVideo},'%')
and xinxi.xinxi_types = #{params.xinxiTypes}
and xinxi.zan_number >= #{params.zanNumberStart}
and xinxi.zan_number <= #{params.zanNumberEnd}
and xinxi.zan_number = #{params.zanNumber}
and xinxi.cai_number >= #{params.caiNumberStart}
and xinxi.cai_number <= #{params.caiNumberEnd}
and xinxi.cai_number = #{params.caiNumber}
and xinxi.xinxi_clicknum >= #{params.xinxiClicknumStart}
and xinxi.xinxi_clicknum <= #{params.xinxiClicknumEnd}
and xinxi.xinxi_clicknum = #{params.xinxiClicknum}
and xinxi.shangxia_types = #{params.shangxiaTypes}
and xinxi.xinxi_content like CONCAT('%',#{params.xinxiContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.school like CONCAT('%',#{params.school},'%')
and yonghu.shiyong_types = #{params.shiyongTypes}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
